import React, { useState } from 'react';
import '../styles/ProjectScreen.css';
import cameraIcon from '../assets/camera-icon.png'; // Kamera ikonu için bir resim ekleyin

const ProjectScreen = () => {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUploadAndTest = () => {
    if (selectedFile) {
      // Dosya yükleme ve test işlemi burada yapılacak
      console.log('Selected file:', selectedFile);
      // Yapay zeka modeli ile test işlemi burada yapılacak
    } else {
      alert('Please select a file first!');
    }
  };

  return (
    <div className="project-screen-container">
      <h2>Upload and Test</h2>
      <div className="upload-container">
        <input
          type="file"
          id="file-upload"
          accept="image/*"
          onChange={handleFileChange}
          style={{ display: 'none' }}
        />
        <label htmlFor="file-upload" className="upload-label">
          <img src={cameraIcon} alt="Upload" className="camera-icon" />
        </label>
      </div>
      <button onClick={handleUploadAndTest} className="upload-test-button">
        Upload and Test
      </button>
    </div>
  );
};

export default ProjectScreen;
