import React from 'react';
import '../styles/AccountPage.css';

const AccountPage = () => {
  const user = {
    photo: 'https://via.placeholder.com/150', // Kullanıcı fotoğrafının URL'si
    email: 'user@example.com',
    company: 'Example Company',
  };

  const handleChangePassword = () => {
    // Şifre değiştirme işlemi burada yapılacak
    alert('Change Password clicked');
  };

  return (
    <div className="account-page-container">
      <div className="account-info">
        <img src={user.photo} alt="User" className="user-photo" />
        <h2>{user.email}</h2>
        <p>{user.company}</p>
        <button onClick={handleChangePassword} className="change-password-button">
          Change Password
        </button>
      </div>
    </div>
  );
};

export default AccountPage;
